    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        try {
            // TODO add your handling code here:
            String codigo = jTextField1.getText();
            String nombre = jTextField2.getText();
                                   
            Curso curso = new Curso(codigo, nombre);
            MetodosCurso metodosCurso = new MetodosCurso();            
            metodosCurso.agregarCurso(Proyecto_PrograII.ventanaPrincipal.listaCurso, curso);
            JOptionPane.showMessageDialog(null, "Curso Agregado Correctamente");
            
            jTextField1.setText("");
            jTextField2.setText("");
  
        
         }catch(AbstractMethodError error){
            JOptionPane.showMessageDialog(null, "Curso ya existe");
            jTextField1.setText("");
            jTextField2.setText("");
       }
    }//GEN-LAST:event_jButton1ActionPerformed

